import React from 'react';
import { Chart as ChartJs, Title, Tooltip, Legend, ArcElement } from 'chart.js';
import { Line, Pie } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';

ChartJs.register(Title, Tooltip, Legend, ArcElement);

function FinancialOverview() {
    const { incomes, expenses } = useGlobalContext();

    // Calculate total income
    const totalIncome = incomes.reduce((total, income) => total + income.amount, 0);

    // Calculate total expenses
    const totalExpenses = expenses.reduce((total, expense) => total + expense.amount, 0);

    // Calculate total balance
    const totalBalance = totalIncome - totalExpenses;

    // Data for Pie chart
    const data = {
        labels: ['Income', 'Expenses'],
        datasets: [
            {
                label: 'Financial Overview',
                data: [totalIncome, totalExpenses],
                backgroundColor: ['green', 'red'],
            },
        ],
    };

    return (
        <FinancialOverviewContainer>
            <ChartContainer>
                <ChartTitle>Financial Overview</ChartTitle>
                <PieChartWrapper>
                    <PieChart data={data} />
                </PieChartWrapper>
            </ChartContainer>
            <TotalsContainer>
                <TotalBox>
                    <TotalTitle>Total Income</TotalTitle>
                    <TotalAmount>${totalIncome}</TotalAmount>
                </TotalBox>
                <TotalBox>
                    <TotalTitle>Total Expenses</TotalTitle>
                    <TotalAmount>${totalExpenses}</TotalAmount>
                </TotalBox>
                <TotalBox>
                    <TotalTitle>Total Balance</TotalTitle>
                    <TotalAmount>${totalBalance}</TotalAmount>
                </TotalBox>
            </TotalsContainer>
        </FinancialOverviewContainer>
    );
}

const FinancialOverviewContainer = styled.div`
    display: flex;
    justify-content: space-around;
    align-items: flex-start;
    flex-wrap: wrap;
    gap: 20px;
    margin-top: 20px;
`;

const ChartContainer = styled.div`
    flex: 1;
    min-width: 300px;
`;

const ChartTitle = styled.h2`
    text-align: center;
    margin-bottom: 10px;
`;

const PieChartWrapper = styled.div`
    max-width: 300px;
    margin: 0 auto;
`;

const TotalsContainer = styled.div`
    display: flex;
    flex-direction: column;
    gap: 20px;
`;

const TotalBox = styled.div`
    background-color: #f0f0f0;
    padding: 10px;
    border-radius: 8px;
    text-align: center;
`;

const TotalTitle = styled.h3`
    margin: 0;
`;

const TotalAmount = styled.p`
    margin: 5px 0;
    font-size: 18px;
`;

export default FinancialOverview;
